import React, { useState, useEffect } from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import { Link } from 'react-router-dom';
import { GET_ALL_CATEGORIES } from '../api/apiService';

const DropDown = () => {

    const [categories, setCategories] = useState([]);
    useEffect(() => {
        GET_ALL_CATEGORIES(`categories`).then((item) => {
            setCategories(item.data)
        });

    }, []);

    return (
        <Dropdown>
            <Dropdown.Toggle variant='white' id="dropdown-basic">
                <i class="fa fa-bars text-muted mr-2"></i>
                Danh mục
            </Dropdown.Toggle>

            <Dropdown.Menu style={{ width: "180px", padding: "20px" }}>
                <nav class="row" >
                    <div class="col-12">
                        {categories.map((category) => (
                            <Link to={`products/getlatest?categoryid=${category.id}`} style={{ display: "block" }} key={category.id}>{category.name}</Link>
                        ))}
                        <Link to={`products/getlatest`} style={{ display: "block" }}>Tất cả</Link>
                    </div>

                </nav>
            </Dropdown.Menu>
        </Dropdown>
    );
}

export default DropDown;
