import React, { useEffect, useContext } from 'react';
import { UserContext } from '../context/UseContext';
import { Link } from 'react-router-dom';

const CartItem = () => {

    const { count, addCart } = useContext(UserContext);
    useEffect(() => {
        const arrayProduct = sessionStorage.getItem("arrayProduct");
        if (arrayProduct) {
            const parsedArray = JSON.parse(arrayProduct);
            addCart(parsedArray.length);
        }
    }, [count]);

    return (
        <Link to="/ShoppingCart" className="widget-view">
            <div className="icon-area">
                <i className="fa fa-shopping-cart"></i>
                <span className="notify">{count}</span>
            </div>
            <small className="text"> Giỏ hàng </small>
        </Link>
    );
}

export default CartItem;
