import React, { useState, useEffect, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../context/UserContext";
import { PLACE_ORDER } from "../api/apiService";
import Total from "../pages/shoppingcart/Total";

const ShoppingCart = () => {
  const navigate = useNavigate();
  const { user, cart, addCart, removeCart } = useContext(UserContext);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    let totalPrice = 0;
    cart.forEach((item) => {
      if (item.product) {
        totalPrice += item.product.price * item.quantity;
      }
    });
    setTotal(totalPrice);
  }, [cart]);

  // ✅ Function to Increase Quantity
  const increaseQuantity = (product) => {
    addCart(product, 1); // Increase quantity by 1
  };

  // ✅ Function to Decrease Quantity
  const decreaseQuantity = (product) => {
    if (product.quantity > 1) {
      addCart(product, -1); // Decrease quantity by 1
    } else {
      removeCart(product.id); // Remove item if quantity reaches 0
    }
  };

  return (
    <section className="section-content padding-y">
      <div className="container">
        <div className="row">
          <main className="col-md-9">
            <div className="card">
              <table className="table table-borderless table-shopping-cart">
                <thead className="text-muted">
                  <tr className="small text-uppercase">
                    <th scope="col">Sản phẩm</th>
                    <th scope="col" width="120">
                      Số lượng
                    </th>
                    <th scope="col" width="120">
                      Giá
                    </th>
                    <th scope="col" className="text-right" width="200"></th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((item, index) => {
                    if (!item.product) return null;
                    return (
                      <tr key={index}>
                        <td>
                          <figure className="itemside">
                            <figcaption className="info">
                              <a href="#" className="title text-dark">
                                {item.product.productName}
                              </a>
                            </figcaption>
                          </figure>
                        </td>
                        <td>
                          <button
                            className="btn btn-light"
                            onClick={() => decreaseQuantity(item.product)}
                          >
                            -
                          </button>
                          <span className="mx-2">{item.quantity}</span>
                          <button
                            className="btn btn-light"
                            onClick={() => increaseQuantity(item.product)}
                          >
                            +
                          </button>
                        </td>
                        <td>
                          {(item.product.price * item.quantity).toFixed(2)}₫
                        </td>
                        <td className="text-right">
                          <button
                            className="btn btn-light btn-round"
                            onClick={() => removeCart(item.product.id)}
                          >
                            <i className="fas fa-times"></i>
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
              <div className="card-body border-top">
                <button className="btn btn-primary float-md-right">
                  Mua hàng <i className="fa fa-chevron-right"></i>
                </button>
              </div>
            </div>
          </main>
          <Total total={total} />
        </div>
      </div>
    </section>
  );
};

export default ShoppingCart;
