import React, { useState, useEffect, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../context/UserContext";
import { PLACE_ORDER } from "../api/apiService";
import Total from "../pages/shoppingcart/Total";

const ShoppingCart = () => {
  const navigate = useNavigate();
  const { user, cart, addCart, removeCart } = useContext(UserContext);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    let totalPrice = 0;
    cart.forEach((item) => {
      if (item.product) {
        totalPrice += item.product.price * item.quantity;
      }
    });
    setTotal(totalPrice);
  }, [cart]);

  // âœ… Function to Update Quantity in Real Time
  const handleQuantityChange = (product, newQuantity) => {
    const parsedQuantity = parseInt(newQuantity, 10);
    if (parsedQuantity > 0) {
      addCart(product, parsedQuantity - product.quantity); // Adjust difference
    }
  };

  return (
    <section className="section-content padding-y">
      <div className="container">
        <div className="row">
          <main className="col-md-9">
            <div className="card">
              <table className="table table-borderless table-shopping-cart">
                <thead className="text-muted">
                  <tr className="small text-uppercase">
                    <th scope="col">Sáº£n pháº©m</th>
                    <th scope="col" width="120">
                      Sá»‘ lÆ°á»£ng
                    </th>
                    <th scope="col" width="120">
                      GiÃ¡
                    </th>
                    <th scope="col" className="text-right" width="200"></th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((item, index) => {
                    if (!item.product) return null;
                    return (
                      <tr key={index}>
                        <td>
                          <figure className="itemside">
                            <div className="aside">
                              <img
                                src={`/images/${item.product.id}.jpg`}
                                className="img-sm"
                                alt={item.product.productName}
                              />
                            </div>
                            <figcaption className="info">
                              <a href="#" className="title text-dark">
                                {item.product.productName}
                              </a>
                            </figcaption>
                          </figure>
                        </td>
                        <td>
                          {/* âœ… Number Input for Quantity */}
                          <input
                            type="number"
                            className="form-control"
                            value={item.quantity}
                            min="1"
                            max="100"
                            onChange={(e) =>
                              handleQuantityChange(item.product, e.target.value)
                            }
                          />
                        </td>
                        <td>
                          {(item.product.price * item.quantity).toFixed(2)}â‚«
                        </td>
                        <td className="text-right">
                          <button
                            className="btn btn-light btn-round"
                            onClick={() => removeCart(item.product.id)}
                          >
                            <i className="fas fa-times"></i>
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
              <div className="card-body border-top">
                <button className="btn btn-primary float-md-right">
                  Mua hÃ ng <i className="fa fa-chevron-right"></i>
                </button>
              </div>
            </div>
          </main>
          <Total total={total} />
        </div>
      </div>
    </section>
  );
};

export default ShoppingCart;
