import React, { useEffect, useState } from "react";
import Slider from "../pages/home/Slide";
import Category from "../pages/home/Category";
import { GET_ALL_PRODUCTS } from "../api/apiService";

function Home() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    GET_ALL_PRODUCTS()
      .then((allProducts) => {
        if (allProducts.length > 0) {
          // Extract unique categories from the product list
          const uniqueCategories = [];
          allProducts.forEach((product) => {
            if (
              !uniqueCategories.some((cat) => cat.name === product.category)
            ) {
              uniqueCategories.push({ id: product.id, name: product.category });
            }
          });
          setCategories(uniqueCategories);
        }
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, []);

  return (
    <div className="container">
      <Slider />
      {categories.length > 0 &&
        categories.map((category) => (
          <Category
            key={category.id}
            categoryName={category.name}
            categoryId={category.id}
          />
        ))}
    </div>
  );
}

export default Home;
