import React, { useEffect, useState } from "react";
import Slider from "../pages/home/Slide";
import Category from "../pages/home/Category";
import { GET_ALL_PRODUCTS } from "../api/apiService";

function Home() {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);

  // Fetch products when the component mounts
  useEffect(() => {
    GET_ALL_PRODUCTS()
      .then((allProducts) => {
        if (allProducts.length > 0) {
          setProducts(allProducts);

          // Extract unique categories from products
          const uniqueCategories = [];
          allProducts.forEach((product) => {
            if (
              !uniqueCategories.some((cat) => cat.name === product.category)
            ) {
              uniqueCategories.push({
                id: product.id, // Using product ID since no category ID exists
                name: product.category,
              });
            }
          });

          setCategories(uniqueCategories);
        }
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, []);

  return (
    <div className="container">
      <Slider />

      {/* Render categories with filtered products */}
      {categories.length > 0 &&
        categories.map((category) => (
          <Category
            key={category.id}
            categoryName={category.name}
            categoryId={category.id}
            products={products.filter((p) => p.category === category.name)}
          />
        ))}
    </div>
  );
}

export default Home;
