import React, { useEffect, useState } from 'react'
import { GET_ALL } from '../../api/apiService';
import Cart from '../product/Cart';
const Category = (props) => {
    const { categoryName, categoryId } = props;
    const [product, setProduct] = useState([]);
    useEffect(() => {
        GET_ALL(`products/getlatesthome?categoryid=${categoryId}`).then((item) => setProduct(item.data)
        );
    }, [categoryId]);
    console.log()
    return (
        <section class="padding-bottom">
            <header class="section-heading heading-line">
                <h4 class="title-section text-uppercase">{categoryName}</h4>
            </header>

            <div class="row">
                {product && product.length > 0 && product.map((product) => (
                    <Cart product={product} key={product.id} />
                ))}
            </div>
        </section>
    )
}

export default Category
