import React, { useState, useEffect, useContext } from 'react'
import { Link } from 'react-router-dom';
import Total from '../pages/shoppingcart/Total';
import { toast } from 'react-toastify';
import { UserContext } from '../context/UseContext';
import { useNavigate } from 'react-router-dom';
const ShoppingCart = () => {
    const navigate = useNavigate();
    const { addCart } = useContext(UserContext);
    const [total, setTotal] = useState(0);
    const [product, setProduct] = useState(JSON.parse(sessionStorage.getItem("arrayProduct")) || []);
    const numberFormatter = new Intl.NumberFormat('de-DE', {
        style: 'decimal',
        minimumFractionDigits: 0,
        maximumFractionDigits: 0,
    });
    useEffect(() => {
        let totalPrice = 0;
        product.forEach((item) => {
            totalPrice += item.price * item.quantity;
        });
        setTotal(totalPrice);
    }, [product]);
    const handleRemove = (id) => {
        let result = JSON.parse(sessionStorage.getItem("arrayProduct"))
        result = product.filter((product) => product.id != id);
        sessionStorage.setItem("arrayProduct", JSON.stringify(result));
        setProduct(result);
        addCart(result.length);
       
    }
    const handleSelect = (id, event) => {
        let result = JSON.parse(sessionStorage.getItem("arrayProduct"));
        if (result) {
            const updatedResult = result.map((item) =>
                item.id === id ? { ...item, quantity: parseInt(event.target.value, 10) } : item
            );
            setProduct(updatedResult);
            sessionStorage.setItem("arrayProduct", JSON.stringify(updatedResult));
        }
    }
    const handleOrder = () => {
        if (product.length > 0) {
            if (localStorage.getItem("accessToken")) {
                navigate("/InfoOrder");
            }
        }
    }
    return (
        <>
            <section class="section-content padding-y">
                <div class="container">
                    <div class="row">
                        <main class="col-md-9">
                            <div class="card">

                                <table class="table table-borderless table-shopping-cart">
                                    <thead class="text-muted">
                                        <tr class="small text-uppercase">
                                            <th scope="col">Sản phẩm</th>
                                            <th scope="col" width="120">Số lượng</th>
                                            <th scope="col" width="120">Giá</th>
                                            <th scope="col" class="text-right" width="200"> </th>
                                        </tr>
                                    </thead><tbody>
                                        {product.map((item) => {
                                            return (
                                                <tr key={item.id}>
                                                    <td>
                                                        <figure class="itemside">
                                                            <div class="aside"><img src={require(`../assets/images/items/${item.image}`)} class="img-sm" /></div>
                                                            <figcaption class="info">
                                                                <a href="#" class="title text-dark">{item.name}</a>
                                                                <p class="text-muted small">Size: XL, Color: blue, <br /> Brand: Gucci</p>
                                                            </figcaption>
                                                        </figure>
                                                    </td>
                                                    <td>
                                                        <select class="form-control" onChange={(event) => handleSelect(item.id, event)}>
                                                            <option>{item.quantity}</option>
                                                            <option>1</option>
                                                            <option>2</option>
                                                            <option>3</option>
                                                            <option>4</option>
                                                        </select>

                                                    </td>
                                                    <td>
                                                        <div class="price-wrap">
                                                            <var style={{ "color": "red" }} class="price">{numberFormatter.format(item.price * item.quantity)}₫</var>
                                                            <small class="text-muted"> {numberFormatter.format(item.price)}₫ each </small>
                                                        </div>
                                                    </td>
                                                    <td class="text-right">
                                                        <button class="btn btn-light btn-round" onClick={() => handleRemove(item.id)}><i style={{ color: "red" }} class="fas fa-trash"></i></button>
                                                    </td>
                                                </tr>

                                            )
                                        })}


                                    </tbody>
                                </table>
                                <div class="card-body border-top">
                                <button class="btn btn-primary float-md-right" onClick={() => handleOrder()} style={{ backgroundColor: "#ffd400", border: "1px solid #ffd400", color: "black" }}> Mua hàng <i class="fa fa-chevron-right"></i> </button>
                                    <Link to="/" class="btn btn-light"> <i class="fa fa-chevron-left"></i> Tiếp tục mua sắm </Link>
                                </div>
                            </div>
                            <div class="alert alert-success mt-3">
                                <p class="icontext"><i class="icon text-success fa fa-truck"></i>
                                    Giao hàng miễn phí trong vòng 1-2 tuần</p>
                            </div>
                        </main>
                        <Total total={total} />
                    </div>
                </div>
            </section>
        </>
    )
}

export default ShoppingCart
