import React, { useState, useEffect, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../context/UserContext";
import { PLACE_ORDER } from "../api/apiService"; // ✅ Import place order function
import Total from "../pages/shoppingcart/Total";

const ShoppingCart = () => {
  const navigate = useNavigate();
  const { user, cart, removeCart, updateCartQuantity } =
    useContext(UserContext);
  const [total, setTotal] = useState(0);
  const [error, setError] = useState("");

  useEffect(() => {
    let totalPrice = 0;
    cart.forEach((item) => {
      totalPrice += item.price * item.quantity;
    });
    setTotal(totalPrice);
  }, [cart]);

  const handlePlaceOrder = async () => {
    if (!user?.id) {
      alert("Vui lòng đăng nhập để đặt hàng!");
      navigate("/login");
      return;
    }

    try {
      const response = await PLACE_ORDER(user.id);
      if (response) {
        alert("Đơn hàng của bạn đã được đặt thành công!");
        navigate("/orders"); // ✅ Redirect to orders page
      } else {
        setError("Đặt hàng thất bại, vui lòng thử lại!");
      }
    } catch (error) {
      setError("Lỗi khi đặt hàng, vui lòng thử lại!");
    }
  };

  return (
    <section className="section-content padding-y">
      <div className="container">
        <div className="row">
          <main className="col-md-9">
            <div className="card">
              <table className="table table-borderless table-shopping-cart">
                <thead className="text-muted">
                  <tr className="small text-uppercase">
                    <th scope="col">Sản phẩm</th>
                    <th scope="col" width="120">
                      Số lượng
                    </th>
                    <th scope="col" width="120">
                      Giá
                    </th>
                    <th scope="col" className="text-right" width="200"></th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((item) => (
                    <tr key={item.id}>
                      <td>
                        <figure className="itemside">
                          <div className="aside">
                            <img
                              src={`/images/${item.image}`}
                              className="img-sm"
                              alt={item.productName}
                            />
                          </div>
                          <figcaption className="info">
                            <a href="#" className="title text-dark">
                              {item.productName}
                            </a>
                          </figcaption>
                        </figure>
                      </td>
                      <td>
                        <select
                          className="form-control"
                          value={item.quantity}
                          onChange={(e) =>
                            updateCartQuantity(
                              item.id,
                              parseInt(e.target.value)
                            )
                          }
                        >
                          {[...Array(10).keys()].map((num) => (
                            <option key={num + 1} value={num + 1}>
                              {num + 1}
                            </option>
                          ))}
                        </select>
                      </td>
                      <td>
                        <div className="price-wrap">
                          <var className="price">
                            {item.price * item.quantity}₫
                          </var>
                          <small className="text-muted">
                            {item.price}₫ each
                          </small>
                        </div>
                      </td>
                      <td className="text-right">
                        <button
                          className="btn btn-light btn-round"
                          onClick={() => removeCart(item.id)}
                        >
                          <i className="fas fa-times"></i>
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <div className="card-body border-top">
                {error && <div className="alert alert-danger">{error}</div>}
                <button
                  className="btn btn-primary float-md-right"
                  onClick={handlePlaceOrder}
                >
                  Đặt hàng <i className="fa fa-chevron-right"></i>
                </button>
                <Link to="/" className="btn btn-light">
                  <i className="fa fa-chevron-left"></i> Tiếp tục mua sắm
                </Link>
              </div>
            </div>
          </main>
          <Total total={total} />
        </div>
      </div>
    </section>
  );
};

export default ShoppingCart;
