// export { UserContext, UserProvider };
import React, { createContext, useState, useEffect } from "react";
import { GET_CART, PLACE_ORDER } from "../api/apiService";

const UserContext = createContext();

const UserProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [cart, setCart] = useState([]);

  // ✅ Load user from localStorage
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) setUser(storedUser.data);
  }, []);

  // ✅ Fetch Cart Data After Login
  useEffect(() => {
    if (user?.id) {
      GET_CART().then((response) => {
        if (response) {
          setCart(response);
        }
      });
    }
  }, [user?.id]);

  // ✅ Function to Place Order (Remove `useNavigate`)
  const placeOrder = async () => {
    if (!user?.id) {
      return { error: "Bạn cần đăng nhập để đặt hàng!" };
    }

    await PLACE_ORDER(user.id);
    setCart([]); // ✅ Clear cart after placing order
    return { success: "Đặt hàng thành công!" };
  };

  return (
    <UserContext.Provider value={{ user, cart, placeOrder }}>
      {children}
    </UserContext.Provider>
  );
};

export { UserContext, UserProvider };
