import React from "react";


const UserProvider = ({ children }) => {
    const [count, setCount] = React.useState(0);



    const addCart = (newCount) => {
        setCount(newCount);
    };

    return (
        <UserContext.Provider value={{ addCart, count }}>
            {children}
        </UserContext.Provider>
    )
}

export { UserProvider }
