import React, { createContext, useState, useEffect } from "react";
import { GET_CART, ADD_TO_CART, REMOVE_FROM_CART } from "../api/apiService";

const UserContext = createContext();

const UserProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [cart, setCart] = useState([]);
  const [userId, setUserId] = useState(localStorage.getItem("userId") || null);

  // Load user from localStorage
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) setUser(storedUser.data);
  }, []);

  // Load cart from API on component mount
  useEffect(() => {
    if (!userId) {
      navigate("/login");
    } else {
      GET_CART(userId).then((response) => {
        if (response && response.data) {
          setCart(response.data);
        }
      });
    }
  }, [userId, navigate]);

  // Function to add product to cart
  const addCart = (product, quantity = 1) => {
    const cartId = localStorage.getItem("cartId") || "guest";
    ADD_TO_CART(product.id, quantity, cartId).then(() => {
      setCart([...cart, { ...product, quantity }]);
    });
  };

  // Function to remove product from cart
  const removeCart = (productId) => {
    const cartId = localStorage.getItem("cartId") || "guest";
    REMOVE_FROM_CART(productId, cartId).then(() => {
      setCart(cart.filter((item) => item.id !== productId));
    });
  };

  return (
    <UserContext.Provider value={{ user, cart, addCart, removeCart }}>
      {children}
    </UserContext.Provider>
  );
};

export { UserContext, UserProvider };
