import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import Total from "../pages/shoppingcart/Total";
import { UserContext } from "../context/UseContext";
import { useNavigate } from "react-router-dom";
const ShoppingCart = () => {
  const navigate = useNavigate();
  const { addCart } = useContext(UserContext);
  const [total, setTotal] = useState(0);
  const [product, setProduct] = useState(
    JSON.parse(sessionStorage.getItem("arrayProduct")) || []
  );

  useEffect(() => {
    let totalPrice = 0;
    product.forEach((item) => {
      totalPrice += item.price * item.quantity;
    });
    setTotal(totalPrice);
  }, [product]);
  const handleRemove = (id) => {
    let result = JSON.parse(sessionStorage.getItem("arrayProduct"));
    result = product.filter((product) => product.id != id);
    sessionStorage.setItem("arrayProduct", JSON.stringify(result));
    setProduct(result);
    addCart(result.length);
  };
  const handleSelect = (id, event) => {
    let result = JSON.parse(sessionStorage.getItem("arrayProduct"));
    if (result) {
      const updatedResult = result.map((item) =>
        item.id === id
          ? { ...item, quantity: parseInt(event.target.value, 10) }
          : item
      );
      setProduct(updatedResult);
      sessionStorage.setItem("arrayProduct", JSON.stringify(updatedResult));
    }
  };
  const handleOrder = () => {
    if (product.length > 0) {
      navigate("/FormOrder");
    }
  };
  return (
    <>
      <section class="section-content padding-y">
        <div class="container">
          <div class="row">
            <main class="col-md-9">
              <div class="card">
                <table class="table table-borderless table-shopping-cart">
                  <thead class="text-muted">
                    <tr class="small text-uppercase">
                      <th scope="col">Sản phẩm</th>
                      <th scope="col" width="120">
                        Số lượng
                      </th>
                      <th scope="col" width="120">
                        Giá
                      </th>
                      <th scope="col" class="text-right" width="200">
                        {" "}
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {product.map((item) => {
                      return (
                        <tr key={item.id}>
                          <td>
                            <figure class="itemside">
                              <div class="aside">
                                <img
                                  src={`/images/items/${item.image}`} // ✅ Use public folder path
                                  className="img-sm"
                                  alt={item.name}
                                />
                              </div>
                              <figcaption class="info">
                                <a href="#" class="title text-dark">
                                  {item.name}
                                </a>
                                <p class="text-muted small">
                                  Size: XL, Color: blue, <br /> Brand: Gucci
                                </p>
                              </figcaption>
                            </figure>
                          </td>
                          <td>
                            <select
                              class="form-control"
                              onChange={(event) => handleSelect(item.id, event)}
                            >
                              <option>{item.quantity}</option>
                              <option>1</option>
                              <option>2</option>
                              <option>3</option>
                              <option>4</option>
                            </select>
                          </td>
                          <td>
                            <div class="price-wrap">
                              <var style={{ color: "black" }} class="price">
                                {item.price * item.quantity}₫
                              </var>
                              <small class="text-muted">
                                {" "}
                                {item.price}₫ each{" "}
                              </small>
                            </div>
                          </td>
                          <td class="text-right">
                            <button
                              class="btn btn-light btn-round"
                              onClick={() => handleRemove(item.id)}
                            >
                              <i class="fas fa-times"></i>
                            </button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
                <div class="card-body border-top">
                  <button
                    class="btn btn-primary float-md-right"
                    onClick={() => handleOrder()}
                    style={{
                      backgroundColor: "pink",
                      border: "1px solid #ffd400",
                      color: "black",
                    }}
                  >
                    {" "}
                    Mua hàng <i class="fa fa-chevron-right"></i>{" "}
                  </button>
                  <Link to="/" class="btn btn-light">
                    {" "}
                    <i class="fa fa-chevron-left"></i> Tiếp tục mua sắm{" "}
                  </Link>
                </div>
              </div>
            </main>
            <Total total={total} />
          </div>
        </div>
      </section>
    </>
  );
};

export default ShoppingCart;
