import axios from "axios";
let API_URL = "http://localhost:8080/api";
export function callApi(endpoint, method = 'GET', body) {
    return axios({
        method,
        url: `${API_URL}/${endpoint}`,
        data: body,
    }).catch(e => {
        console.log(e)
    })
}
export function GET_ALL_PRODUCTS(endpoint) {
    return callApi(endpoint, "GET");
}
export function GET_PRODUCT_ID(endpoint, id) {
    return callApi(endpoint + "/" + id, "GET");
}
export function POST_ADD_PRODUCT(endpoint, data) {
    return callApi(endpoint, "POST", data);
}
export function PUT_EDIT_PRODUCT(endpoint, data) {
    return callApi(endpoint, "PUT", data);
}
export function DELETE_PRODUCT_ID(endpoint) {
    return callApi(endpoint, "DELETE");
}
export function GET_ALL_CATEGORIES(endpoint) {
    return callApi(endpoint, "GET");
}

export function GET_CATEGORY_ID(endpoint, id) {
    return callApi(endpoint + "/" + id, "GET");
}
export function GET_USER(endpoint, data) {
    return callApi(endpoint, "POST", data);
}

export function GET_USER_ID(endpoint, id) {
    return callApi(endpoint + "/" + id, "GET");
}

export function GET_ALL(endpoint) {
    return callApi(endpoint, "GET");
}
export function GET_PRODUCT_RANDOM(endpoint) {
    return callApi(endpoint, "GET");
}


export function POST_ADD_ORDER(endpoint, data) {
    return callApi(endpoint, "POST", data);
}

export function POST_ADD_ORDERDTAIL(endpoint, data) {
    return callApi(endpoint, "POST", data);
}

export function POST_ADD_USER(endpoint, data) {
    return callApi(endpoint, "POST", data);
}
