import React, { useState, useEffect, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../context/UserContext";
import { PLACE_ORDER } from "../api/apiService"; // ✅ Import Place Order API
import Total from "../pages/shoppingcart/Total";

const ShoppingCart = () => {
  const navigate = useNavigate();
  const { user, cart, removeCart, updateCartQuantity } =
    useContext(UserContext);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    let totalPrice = 0;
    cart.forEach((item) => {
      totalPrice += item.price * item.quantity;
    });
    setTotal(totalPrice);
  }, [cart]);

  // ✅ Function to Place Order
  const handlePlaceOrder = async () => {
    if (!user) {
      alert("Bạn cần đăng nhập để đặt hàng!");
      navigate("/login");
      return;
    }

    const response = await PLACE_ORDER();
    if (response) {
      alert("Đặt hàng thành công!");
      navigate("/"); // ✅ Redirect to homepage
    } else {
      alert("Đặt hàng thất bại, vui lòng thử lại.");
    }
  };

  return (
    <section className="section-content padding-y">
      <div className="container">
        <div className="row">
          <main className="col-md-9">
            <div className="card">
              <table className="table table-borderless table-shopping-cart">
                <thead className="text-muted">
                  <tr className="small text-uppercase">
                    <th scope="col">Sản phẩm</th>
                    <th scope="col" width="120">
                      Số lượng
                    </th>
                    <th scope="col" width="120">
                      Giá
                    </th>
                    <th scope="col" className="text-right" width="200"></th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((item) => (
                    <tr key={item.id}>
                      <td>
                        <figure className="itemside">
                          <div className="aside">
                            <img
                              src={`/images/${item.image}`}
                              className="img-sm"
                              alt={item.productName}
                            />
                          </div>
                          <figcaption className="info">
                            <a href="#" className="title text-dark">
                              {item.productName}
                            </a>
                          </figcaption>
                        </figure>
                      </td>
                      <td>{item.quantity}</td>
                      <td>{item.price * item.quantity}₫</td>
                      <td className="text-right">
                        <button
                          className="btn btn-light btn-round"
                          onClick={() => removeCart(item.id)}
                        >
                          <i className="fas fa-times"></i>
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <div className="card-body border-top">
                <button
                  className="btn btn-primary float-md-right"
                  onClick={handlePlaceOrder}
                >
                  Mua hàng <i className="fa fa-chevron-right"></i>
                </button>
              </div>
            </div>
          </main>
          <Total total={total} />
        </div>
      </div>
    </section>
  );
};

export default ShoppingCart;
