// export default ShoppingCart;
import React, { useContext } from "react";
import { UserContext } from "../context/UserContext";
import { Link, useNavigate } from "react-router-dom";
import { PLACE_ORDER } from "../api/apiService";

const ShoppingCart = () => {
  const { cart, removeFromCart, user } = useContext(UserContext);
  const navigate = useNavigate();

  // Calculate total price
  const totalPrice = cart.reduce(
    (acc, item) => acc + item.price * item.quantity,
    0
  );

  // Handle order placement
  const handleOrder = () => {
    if (user) {
      const cartId = localStorage.getItem("cartId") || "guest";
      PLACE_ORDER(user.id, cartId).then((response) => {
        if (response) {
          alert("Order placed successfully!");
          navigate("/order-success");
        }
      });
    } else {
      alert("You need to log in first!");
      navigate("/login");
    }
  };

  return (
    <section className="section-content padding-y">
      <div className="container">
        <div className="row">
          <main className="col-md-9">
            <div className="card">
              <table className="table table-borderless table-shopping-cart">
                <thead className="text-muted">
                  <tr className="small text-uppercase">
                    <th scope="col">Sản phẩm</th>
                    <th scope="col" width="120">
                      Số lượng
                    </th>
                    <th scope="col" width="120">
                      Giá
                    </th>
                    <th scope="col" width="120"></th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((item) => (
                    <tr key={item.id}>
                      <td>{item.productName}</td>
                      <td>{item.quantity}</td>
                      <td>{item.price * item.quantity}₫</td>
                      <td>
                        <button
                          className="btn btn-danger"
                          onClick={() => removeFromCart(item.id)}
                        >
                          Xóa
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <div className="card-body border-top">
                <button className="btn btn-primary" onClick={handleOrder}>
                  Đặt hàng
                </button>
                <Link to="/" className="btn btn-light">
                  Tiếp tục mua sắm
                </Link>
              </div>
            </div>
          </main>
          <aside className="col-md-3">
            <div className="card">
              <div className="card-body">
                <h4>Tổng tiền: {totalPrice}₫</h4>
              </div>
            </div>
          </aside>
        </div>
      </div>
    </section>
  );
};

export default ShoppingCart;
