import React, { useEffect, useState } from "react";
import Slider from "../pages/home/Slide";
import Category from "../pages/home/Category";
import { GET_ALL_PRODUCTS } from "../api/apiService";

function Home() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    GET_ALL_PRODUCTS()
      .then((response) => {
        console.log("tao", response);
        if (response && response.data) {
          // Extract unique categories from products
          const uniqueCategories = [];
          response.data.forEach((product) => {
            if (
              !uniqueCategories.some((cat) => cat.id === product.categoryId)
            ) {
              uniqueCategories.push({
                id: product.categoryId,
                name: product.categoryName || `Category ${product.categoryId}`,
              });
            }
          });
          setCategories(uniqueCategories);
        }
      })
      .catch((error) => console.error("Error fetching products", error));
  }, []);

  return (
    <div className="container">
      <Slider />
      {categories.length > 0 &&
        categories.map((category) => (
          <Category
            key={category.id}
            categoryName={category.name}
            categoryId={category.id}
          />
        ))}
    </div>
  );
}

export default Home;
