import React, { useEffect, useState } from 'react'
import Slide from '../pages/home2/Slide'
import Daily from '../pages/home2/Daily'
import Banner from '../pages/home2/Banner'
import Looking from '../pages/home2/Looking'
import { useLocation } from 'react-router-dom';
import { GET_ALL_PRODUCTS, GET_ALL, GET_CATEGORY_ID } from "../api/apiService";


import Page3 from '../pages/Page3'
function Product() {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const categoryId = searchParams.get('categoryid');
    const [category, setCategory] = useState("");
    const [product, setProduct] = useState([]);
    useEffect(() => {

        if (categoryId === null) {
            GET_ALL_PRODUCTS(`products/getlatest`).then((item) => setProduct(item.data));
            setCategory("");
        }
        else {
            GET_ALL(`products/getlatest?categoryid=${categoryId}`).then((item) => setProduct(item.data));
            GET_CATEGORY_ID(`categories`, categoryId).then((item) => setCategory(item.data));
        }
    }, [categoryId]);
    console.log("ccc");
    return (
        <div className='container'>
            <Slide />
            {/* <PaginatedItems product={product} categoryId={categoryId} category={category} /> */}
            {/* <PaginatedItems2 /> */}
            <Page3 product={product} category={category} />
            <Banner />
            <Daily />
            <Looking />
        </div>
    )
}

export default Product
